import {useState} from "react";


const Counter = () => {
    let [contadorValue,contadorFuncion] = useState(0);
    return (
        <div>
            <button onClick = {()=>{
                contadorFuncion(contadorValue-1);
            }}>
            Decrementar
            </button>

            <span>{contadorValue}</span>

            <button onClick = {()=>{
                contadorFuncion(contadorValue+1);
            }}>
            Incrementar
            </button>

        </div>
    )
}



export default Counter;
